package problem0467

import (
	"fmt"
	"testing"

	"github.com/stretchr/testify/assert"
)

// tcs is testcase slice
var tcs = []struct {
	p   string
	ans int
}{

	{
		"",
		259259,
	},

	{
		"",
		33475,
	},

	{
		"abce",
		7,
	},

	{
		"abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz",
		2379,
	},

	{
		"abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz",
		1027,
	},

	{
		"abcdcde",
		13,
	},

	{
		"abcdabc",
		10,
	},

	{
		"abcd",
		10,
	},

	{
		"a",
		1,
	},

	{
		"cac",
		2,
	},

	{
		"zab",
		6,
	},

	// 可以有多个 testcase
}

func Test_findSubstringInWraproundString(t *testing.T) {
	ast := assert.New(t)

	for _, tc := range tcs {
		fmt.Printf("~~%v~~\n", tc)
		ast.Equal(tc.ans, findSubstringInWraproundString(tc.p), "输入:%v", tc)
	}
}

func Benchmark_findSubstringInWraproundString(b *testing.B) {
	for i := 0; i < b.N; i++ {
		for _, tc := range tcs {
			findSubstringInWraproundString(tc.p)
		}
	}
}
