func Test_deckRevealedIncreasing(t *testing.T) {
	ast := assert.New(t)

	for _, tc := range tcs {
		ast.Equal(tc.ans, deckRevealedIncreasing(tc.deck), "输入:%v", tc)
	}
}

func Benchmark_deckRevealedIncreasing(b *testing.B) {
	for i := 0; i < b.N; i++ {
		for _, tc := range tcs {
			deckRevealedIncreasing(tc.deck)
		}
	}
}
