func Test_commonChars(t *testing.T) {
	ast := assert.New(t)

	for _, tc := range tcs {
		ans := commonChars(tc.A)
		sort.Strings(ans)
		ast.Equal(tc.ans, ans, "输入:%v", tc)
	}
}

func Benchmark_commonChars(b *testing.B) {
	for i := 0; i < b.N; i++ {
		for _, tc := range tcs {
			commonChars(tc.A)
		}
	}
}
