func Test_longestWPI(t *testing.T) {
	ast := assert.New(t)

	for _, tc := range tcs {
		ast.Equal(tc.ans, longestWPI(tc.hours), "输入:%v", tc)
	}
}

func Benchmark_longestWPI(b *testing.B) {
	for i := 0; i < b.N; i++ {
		for _, tc := range tcs {
			longestWPI(tc.hours)
		}
	}
}
